  select t.id
	 from modeTreeField t
	where ','|| t.allSuperFieldId||',' like #{appIdSql} or t.id = #{appId}

select t.id
from modeTreeField t
where ','+t.allSuperFieldId+',' like #{appIdSql} or t.id = #{appId}

select t.id
from modeTreeField
where concat(',',t.allSuperFieldId,',') like #{appIdSql} or t.id = #{appId}
